import React, { useRef, useState, useEffect, useCallback } from "react";

import "./Pending.css";
import "../../commonCss/table.css"

function Pending() {

    let [users, setUsers] = useState([]);
    let [requests,setRequests] = useState([]);
    const { current } = useRef(users);
    const { currenttwo: requeststwo } = useRef(requests);


const getUsers = useCallback(async () => {

  await fetch("http://localhost:3001/user/makeRequest/getAllUsers")
  .then(d=>d.json())
  .then(d=> {
    setUsers(d);
    console.log(users);
  }).catch(e=>console.log(e.message));


  await fetch("http://localhost:3001/user/makeRequest/getAllRequests")
  .then(d=>d.json())
  .then(d=> {
    setRequests(d);
    console.log(requests);
  }).catch(e=>console.log(e.message));
},[current, currenttwo]);

useEffect(() => {

getUsers();

},[getUsers]);

//     async function getRequests() {

//       await fetch("http://localhost:3001/user/makeRequest/getAllRequests")
//       .then(d=>d.json())
//       .then(d=> {
//         requests = d;
//         console.log(requests);
//       }).catch(e=>console.log(e.message));
//     }
// getRequests();
    async function handleApprove(id) {
      console.log(id);
    }

    async function handleReject(id) {
      console.log(id);
    }

    return(
        <div className="pendingContainer">
            <div className="pendingTitle">
                <h1>Reporting Screen: Pending Requests</h1>
            </div>
            <div className="row">
        <table id="performanceTable" className="rwd-table">
          <thead>
          <tr>
            <th>Request Date</th>
            <th>Request Hour</th>
            <th>Individual Name</th>
            <th>Approve Request</th>
            <th>Reject Request</th>
          </tr>
          </thead>
          <tbody>
          {

              requests.map(request => {
                for(let i=0;i<users.length;i++) {
                    if(request.userId === users[i]._id) {
                      return (
                        <tr key={request._id}>
              <td data-th="Request Date">{request.scheduledate}</td>
              <td data-th="Request Hour">{request.hourschedule}</td>
              <td data-th="Individual Name">{users[i].name}</td>
              <td data-th="Approve Request">
                <button onClick={()=> handleApprove(request._id)} type="submit" className="btn btn-success" >Approve</button>
              </td>
              <td data-th="Reject Request">
                <button onClick={()=> handleReject(request._id)}  type="submit" className="btn btn-warning" >Reject</button>
              </td>
            </tr>
                      )
                    }
                }
                return true;
})
          }



          </tbody>
        </table>
      </div>
        </div>

    );
}

export default Pending;
