import React, {useState, useEffect} from "react";

import "./Updates.css";
import "../../commonCss/table.css";



async function getData() {
  let data = [];
    await fetch("http://localhost:3001/user/makeRequest/getAllUsers")
    .then(d=>d.json())
    .then(d=> {
      data = d;
    }).catch(e=>console.log(e.message));

    return data;

  }

  async function getData2() {
    let data = [];
    await fetch("http://localhost:3001/user/makeRequest/getAllRequests")
  .then(d=>d.json())
  .then(d=> {
    data = d;

  }).catch(e=>console.log(e.message));
  return data;
  }





function Updates() {
  let [users, setUsers] = useState([]);
  let [requests,setRequests] = useState([]);

  useEffect(() => { 


    getData().then(setUsers);

    },[]);

    useEffect(() => {

      getData2().then(setRequests);
    },[]);

    return(
        <div className="updatesContainer">
            <div className="updatesTitle">
                <h1>Reporting Screen: Updates Requests</h1>
            </div>
            <div className="row">
        <table id="performanceTable" className="rwd-table">
          <thead>
          <tr>
            <th>Insurance Id</th>
            <th>Request Date</th>
            <th>Individual Name</th>
            <th>Request Status</th>
          </tr>
          </thead>
          <tbody>

          <tr>
            <td data-th="Insurance Id">45627891</td>
            <td data-th="Request Date">12/12/2021</td>
            <td data-th="Individual Name">Kamali Mugabo</td>
            <td data-th="Request Status">
                <button id="deleteDvo02" type="submit" className="feedbackContainer btn btn-info"  disabled>Your request has been approved, for more information reach out to customer support.</button>
            </td>
          </tr>


          <tr>
            <td data-th="Insurance Id">45627891</td>
            <td data-th="Request Date">12/12/2021</td>
            <td data-th="Individual Name">Kamali Mugabo</td>
            <td data-th="Request Status">
                <button id="deleteDvo02" type="submit" className="feedbackContainer btn btn-danger"  disabled>Your request has been rejected, for more information reach out to customer support.</button>
            </td>
          </tr>



          </tbody>
        </table>
      </div>
        </div>


    );
}

export default Updates;
